import React from 'react'
import MovieCard from './MovieCard'

const MovieList = ({title, movies}) => {
    console.log(movies)

    if (!movies) {
        return <div>Loading...</div>; // Render a loading indicator if movies is null
      }
    
      // Check if movies is an empty array
      if (movies.length === 0) {
        return <div>No movies available</div>; // Render a message if movies is empty
      }
    
  return (
    <div>
        <div>
          <h1>{title}</h1>  
            <div>
                 
                <MovieCard posterPath={movies[0]?.poster_path} />
            </div>
        </div>
    </div>
  )
}

export default MovieList
